CREATE TABLE IF NOT EXISTS `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user` varchar(120) NOT NULL,
  `pwd` varchar(120) NOT NULL,
  `phonenum` varchar(12) NOT NULL,
  `lead` varchar(120) NOT NULL COMMENT '作者导语',
  `pic` varchar(150) NOT NULL COMMENT '作者头像',
  `sex` varchar(10) NOT NULL COMMENT '用户性别',
  `age` int(11) NOT NULL COMMENT '用户年龄',
  `work` varchar(120) NOT NULL COMMENT '用户工作',
  `decs` varchar(200) NOT NULL COMMENT '用户自我介绍',
  `u_like_a` varchar(150) NOT NULL COMMENT '用户喜欢的文章',
  `u_unlike_a` varchar(150) NOT NULL COMMENT '用户不喜欢的文章',
  `state` int(11) NOT NULL,
  `last_ip` varchar(100) NOT NULL,
  `last_time` datetime NOT NULL,
  `login_num` int(11) NOT NULL,
  `level_id` int(11) NOT NULL,
  `date` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=15 ;

